// 引入
// 创建实例
import axios from "axios";

const http = axios.create({
	// 通用请求的地址前缀
	baseURL: '/api',  //当前启动项http://localhost:8080/
	// 超时时间是最大的请求时间，毫秒
	timeout:10000,
})

// 添加请求拦截器
// interceptors是绑定在当前axios的实例http上的
http.interceptors.request.use(function (config) {
    // 在发送请求之前做些什么
    return config;
  }, function (error) {
    // 对请求错误做些什么
    return Promise.reject(error);
  });

// 添加响应拦截器
http.interceptors.response.use(function (response) {
    // 对响应数据做点什么
    return response;
  }, function (error) {
    // 对响应错误做点什么
    return Promise.reject(error);
  });

export default http
